
import React, {Component} from 'react'
import {connect} from 'react-redux'
import * as R from 'ramda'
import {Link} from 'react-router-dom'

import {fetchProductById, addProductToBasket} from 'actions'
import {getProductById} from 'selectors'
import BasketCart from 'components/basketCart'

class Product extends Component {
  componentDidMount() {
    const productId = this.props.match.params.id
    this.props.fetchPhoneById(productId)
  }

  renderField() {
    const {product} = this.props
    const columnField = R.compose(
      R.toPairs,
      R.pick([
        'id',
        'title',
        'store',
        'description',
        'price'
      ])
    )(product)

    return columnField.map(([key, value]) => (
      <div className='column' key={key}>
        <div className='ab-details-title'>
          <p>{key}</p>
        </div>
        <div className='ab-details-info'>
          {value}
        </div>
      </div>
    ))
  }

  renderContent() {
    const {product} = this.props

    return (
      <div className='thumbnail'>
        <div className='row'>
          <div className='col-md-6'>
            <img
              className='img-thumbnail'
              src={product.image}
              alt={product.title}
            />
          </div>
          <div className='col-md-6'>
            {this.renderField()}
          </div>
        </div>
        <div className='caption-full'>
          <h4 className='pull-right'>${product.price}</h4>
          <h4>{product.title}</h4>
          <p>{product.description}</p>
        </div>
      </div>
    )
  }

  renderSidebar() {
    const {product, addProductToBasket} = this.props
    return (
      <div>
        <p className='lead'>Quick shop</p>
        <BasketCart/>
        <div className='form-group'>
          <h1>{product.title}</h1>
          <h2>${product.price}</h2>
        </div>
        <Link to='/'  className='btn btn-info btn-block'>
          Back to store
        </Link>
        <button
          type='button'
          className='btn btn-success btn-block'
          onClick={() => addProductToBasket(product.id)}
        >
          Add to cart
        </button>
      </div>
    )
  }

  render() {
    return (
      <div className='view-container'>
        <div className='container'>
          <div className='row'>
            <div className='col-md-9'>
              {this.props.product && this.renderContent()}
            </div>
            <div className='col-md-3'>
              {this.props.product && this.renderSidebar()}
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    phone: getProductById(state, state.productPage.id)
  }
}

const mapDispatchToProps = {
  fetchProductById,
  addProductToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Product)