import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router-dom'
import * as R from 'ramda'

import {fetchProducts, loadMoreProducts, addProductToBasket} from 'actions'
import {getProducts} from 'selectors'
import Layout from 'containers/layout'

class Products extends Component {
  componentDidMount() {
    this.props.fetchProducts()
  }

  renderProduct(product, index) {
    const {addProductToBasket} = this.props
    const shortDescription = `${R.take(60, product.description)}...`

    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <img
            className='img-thumbnail'
            src={product.image}
            alt={product.title}
          />
          <div className='caption'>
            <h4 className='pull-right'>${product.price}</h4>
            <h4>
              <Link to={`/products/${product.id}`}>
                {product.name}
              </Link>
            </h4>
            <p>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn btn-primary'
                onClick={() => addProductToBasket(product.id)}
              >
                Buy Now!
              </button>
              <Link
                to={`/products/${product.id}`}
                className='btn btn-default'
              >
                More info
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }

  render() {
    const {products, loadMoreProducts} = this.props
    return (
      <Layout>
        <div className='books row'>
          {products.map((products, index) => this.renderProduct(products, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button
              onClick={loadMoreProducts}
              className='pull-right btn btn-primary'
            >
              Load More
            </button>
          </div>
        </div>
      </Layout>
    )
  }
}

const mapStateToProps = state => ({
  products: getProducts(state)
})

const mapDispatchToProps = {
  fetchProducts,
  loadMoreProducts,
  addPhoneToBasket
}

export default connect(mapStateToProps, mapDispatchToProps)(Products)


