import * as R from 'ramda'

const getProductById = (state,id) => R.prop(id, state.products)

export const getProducts = state => {
    const products = R.map(id => getPhoneById(state, id), state.productPage.ids)
    return products
}

export const getRenderedProductsLength = state =>R.length(state.productsPage.ids)
